<?php


namespace App\Repositories\Api;

use App\Http\Responses\ApiResponse;
use App\Services\Transformers\TransactionTransFormer;
use App\Transaction;
use App\User;
use Carbon\Carbon;
use \Auth;
use ReceiptValidator\iTunes\Validator as iTunesValidator;
use Illuminate\Support\Facades\Mail;

class PaymentRepository extends BaseRepository
{
    private $response ,$transactionTransFormer;
    
    public function __construct(
                                ApiResponse $apiResponse,
                                TransactionTransFormer $transactionTransFormer){
        $this->response = $apiResponse;
        $this->transactionTransFormer = $transactionTransFormer;
    }

    /**
     * @param $data
     * @return \Illuminate\Http\JsonResponse|void
     * @throws \ReceiptValidator\RunTimeException
     */
    public function createPayment($data)
    {
        $user = Auth::user();
//        $validator = new iTunesValidator(iTunesValidator::ENDPOINT_PRODUCTION);
        $validator = new iTunesValidator(iTunesValidator::ENDPOINT_SANDBOX);
        $receiptBase64Data = $data['token'];
//        $receiptBase64Data = '';
        try {
            $response = $validator->validate($receiptBase64Data, isset($data['secret_key']) ? $data['secret_key'] : null);
        } catch (Exception $e) {
            
            return $this->response->error($e->getMessage());
        }

        if ($response->isValid()) {
//            $bubble = array();
            foreach ($response->getReceipt()['in_app'] as $value){
                if(!isset($bubble) || new Carbon($bubble['purchase_date']) < new Carbon($value['purchase_date'])){
                    $bubble = $value;
                }
            }

            $transactionData = $bubble;
            switch ($transactionData['product_id']){
                case Transaction::PAYMENT_METHOD['com.gss.monthly'];
                    $user->expired_at =  new Carbon('1 month');
                    break;
                case Transaction::PAYMENT_METHOD['com.gss.quarterly'];
                    $user->expired_at =  new Carbon('3 month');
                    break;
                case Transaction::PAYMENT_METHOD['com.gss.yearly'];
                    $user->expired_at =  new Carbon('1 year');
                    break;
                case Transaction::PAYMENT_METHOD['com.mindmobapp.download'];
                    $user->expired_at =  new Carbon('0 day');
                    break;
            }

            $transactionData['user_id'] = null;
            $transaction = Transaction::create($transactionData);
            
            return $this->response->show($transaction, $this->transactionTransFormer);

        } else {
            
            return $this->response->error('Receipt is not valid.' . '  ' . $response->getResultCode());
        }
    }



    public function sendConfirmationEmail($user)
    {
        $msgData = [
            'firstName' => $user->first_name,
            'lastName' => $user->last_name,
        ];

        Mail::send('emails.premium_register',$msgData,function($msg) use ($user)
        {
            $msg->from('support@pallitapp.com', '"Pallitapp Support');

            $msg->to($user->email, $user->fullName())->subject('Activation of Premium type');
        });
        
        return true;
    }

    public function makePremium($data)
    {
        $transaction = Transaction::where('transaction_id',$data['transaction_id'])->first();
        
        if ($transaction) {
            $transaction->update([ 'user_id'=>Auth::id() ]);
            User::where('id',Auth::id())->update([
                'expired_at'=>$transaction->expires_date,
                'type'=>1
            ]);
        } else {
            
            return $this->response->error('Please enter a valid transaction data');
        }

        return $this->response->show($transaction, $this->transactionTransFormer);
    }
}